import styled from "styled-components";

interface Props {
  scroll: boolean;
}

const section = ({
  className,
  children,
}: {
  className: string;
  children: any;
}) => <section className={className}>{children}</section>;

const nav = ({
  className,
  children,
}: {
  className: string;
  children: any;
}) => <nav className={className}>{children}</nav>;

export const LandingSection = styled.section<Props>`
  width: 100%;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
  background: linear-gradient(
    to right,
    ${(props) => props.theme.primaryColor} 50%,
    ${(props) => props.theme.secondaryColor} 50%
  );
  background-size: 200%, 100%;
  transition: all 2.5s ease;
  background-position: ${({ scroll }) => (scroll ? "right" : "left")};
`;

export const Header = styled.header`
  text-align: center;
  transform: translateY(-20%);
`;

export const NavBar = styled.nav`
    height: 2em;
    position: fixed;
    display: flex;
    right: 0;
    a {
      list-style-type: none;
      display: inline-block;
      margin: 0 2em 0 0;
      color: white;
      font-size: 1rem;
      text-transform: uppercase;
    
      &:target {
        border-bottom: 2px solid ${props => props.theme.highlightColor};
        padding-bottom: 0.1rem;
        right: 100%;
        transition: right .4s cubic-bezier(0,0.5,1);
      }
      &:hover{
        color:  ${props => props.theme.highlightColor};
        border-bottom: 2px solid $highlight-color;
        padding-bottom: 0.1rem;
        right: 0;
      }
    }
`;
