import { FunctionComponent, useContext } from "react";
import ScrollContext from "../../store/context";
import styled from "styled-components";
import "./LandingPage.scss";

interface Props {
  scroll: boolean;
}

const LandingSection = styled.section<Props>`
  width: 100%;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
  background: linear-gradient(
    (to right, $primary-color 50%, $secondary-color 50%)
  );
  background-size: 200%, 100%;
  background-position: ${scroll => scroll ? "right" : "left"};
  @include transition-ease;
`;


const LandingPage: FunctionComponent = () => {
  const scrollCtx = useContext(ScrollContext);
  const scrollState = scrollCtx.scroll;
  // const classes = "landing" + (scrollCtx.scroll ? " night" : "");

  return (
    <LandingSection id="landingPage" scroll={scrollState}>
      <header className="name">
        <h1>I'm John</h1>
        <h2 className={"hidden-header"}>a web developer</h2>
      </header>
    </LandingSection>
  );
};
//
export default LandingPage;
