import { useState, useEffect, useReducer } from "react";
import "./Skills.scss";

const DummyData = [
  {
    id: "s6",
    name: "Typescript",
    num: 6,
  },
  {
    id: "s5",
    name: "React",
    num: 5,
  },
  {
    id: "s4",
    name: "JavaScript",
    num: 4,
  },
  {
    id: "s3",
    name: "SCSS",
    num: 3,
  },
  {
    id: "s2",
    name: "CSS",
    num: 2,
  },
  {
    id: "s1",
    name: "HTML",
    num: 1,
  },
];

const breakPoint = {ONE: "breakpoint_1", TWO:"breakpoint_2", THREE:"breakpoint_3", FOUR:"breakpoint_4", FIVE:"breakpoint_5", SIX:"breakpoint_6"};

const scrollReducer = (state, action:any) => {
  if(action.type === breakPoint.ONE) {

  }
  return { value: "", isValid: false };
};

const Skills = () => {
  const [scroll, setScroll] = useState(false);

  const [scrollState, dispatchScrollState] = useReducer(scrollReducer, {
    value: "",
    isValid: false,
  });

  useEffect(() => {
    const skillSection = document.getElementById("skills");
    const sectionTop = skillSection?.offsetTop;
    const sectionHeight = skillSection?.clientHeight;
    const scrollBreakPoint = sectionHeight! / DummyData.length;

    window.addEventListener("scroll", () => {
      dispatchScrollState({type: 'scroll_state_1'})
      setScroll(window.scrollY > sectionTop! + scrollBreakPoint);
    });
  }, []);

  const scrollCondition = scrollState.value ? "slide-out" : "";
  const skillList = DummyData.map((skill) => (
    <div
      className={`skill-card skill-card-${skill.num} ` + scrollCondition}
      id={`skill${skill.num}`}
    >
      {skill.name}
    </div>
  ));

  return (
    <div className="skills" id="skills">
      <div className="image-container" id="imageContainer">
        {skillList}
      </div>
    </div>
  );
};

export default Skills;
