import "./About.scss";
import styled from "styled-components";
import { useState } from "react";
import { useEffect } from "react";

const AboutMeTitle = styled.div.attrs({
  style: (scrollPercent:number) => ({
    transform: `translateX(${(scrollPercent) * 5.5}%)`,
  }),
})`
  transition: transform 0.5s ease-out;
  position: absolute;
  top:5%;
  left:-15%;
`;

const About = () => {
  const [scrollAmount, setScrollAmount] = useState(0);

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
  })

  // window.removeEventListener("scroll", handleScroll());

  const handleScroll = (event:any) => {
    const {body, documentElement} = event.srcElement;
    const sd = Math.max(body.scrollTop, documentElement.ScrollTop);
    const sp = (sd/(documentElement.scrollHeight - documentElement.clientHeight)*100);
    const maxLimit = (documentElement.clientHeight *150)/ documentElement.scrollHeight;
    if (sp>=0 && sp <=maxLimit) {
      setScrollAmount(sp);
    }
  }

  return (
    <section id="about" className="about">
      <AboutMeTitle scrollPercent={scrollPercent} className="about-title">
        About Me
      </AboutMeTitle>
      <div className="about-me">
        Clean, dynamic Front-end developer creating memorable and smooth user
        experiences
      </div>
    </section>
  );
};

export default About;
