import { useContext, useState } from "react";
import ScrollContext from "../../store/context";
import { Card, CardTitle } from "../UI/Card";
import { CircleHolder } from "./FormButtonStyles";
import { NameInput, ContactForm, MessageInput } from "./FormStyles";

const Form = () => {
  const scrollCtx = useContext(ScrollContext);
  const scroll = scrollCtx.scroll;
  const {form, setForm} = useContext(ScrollContext)

  const formToggle = () => {
    setForm(!form);
  }

  const formCircle = (<>
    <CardTitle>Contact Me!</CardTitle>
    <CircleHolder onClick={formToggle}>
      <div className="circle">Interested?</div>
      <div className="circle"></div>
      <div className="circle"></div>
      <div className="circle"></div>
    </CircleHolder>
    </>
  )

  const formComponent = (
    <>
      <CardTitle>Contact Me!</CardTitle>
      <ContactForm>
        <div className="form-control">
          <div className="name">
            <NameInput type="text" value="Name"/>
          </div>
          <div className="email">
            <NameInput  type="text" value="Email"/>
          </div>
        </div>
        <div className="message">
          <MessageInput type="text" value="Message"/>
        </div>
        <div >
          <button>Cancel</button>
          <button type="submit">Send</button>  
        </div>      
      </ContactForm>
    </>
  )

  return (
    <>
      {scroll && (
        <section id="contact-form">
          <Card>{form ? formComponent : formCircle}</Card>
        </section>
      )}
    </>
  );
};

export default Form;
