import styled, { css, keyframes } from "styled-components";

const circleGenerator = [];

const animateCircle = (i: number) => keyframes`
  0% {
    transform:translateX(-50%) rotate(0deg);
  }
  100% {
    transform:translateX(-50%) rotate(${-i*360}deg);
  }
`;

for (let i = 1; i < 16; i++) {
  if (i > 1) {
    circleGenerator.push(css`
    .circle:nth-child(${i}) {
      size: ${20 * (i - i*0.9)}em;
      border: 13px solid black;
      border-bottom:none;
			border-radius:${20 * (i - i*0.9)}em ${20 * (i - i*0.9)}em 0 0;
			width:${20 * (i - i*0.9)}em;
			height:${(20 * (i - i*0.9)) / 2}em;
			z-index:  ${16 - i};
      // animation: ${animateCircle(i)} 10s infinite ease-in-out;
    }
    `);
  };
};

export const CircleHolder = styled.div`
  width: 500px;
  height:250px;
  position relative;

  .circle {
    border-radius: 100% 100% 0 0;
    position: absolute;
    bottom: 50%;
    left: 50%;
    transform: translateX(-50%);
    transform-origin: bottom center;
  }
  ${circleGenerator}
`;
