import styled, { css, keyframes } from "styled-components";

const circleGenerator = [];

const animateCircle = (i: number) => keyframes`
  0% {
    transform:translateX(-50%) rotate(0deg);
  }
  100% {
    transform:translateX(-50%) rotate(${-i*360}deg);
  }
`;

const initSize = 100;
const sizeMod = 10;

for (let i = 1; i < 16; i++) {
  if (i > 1) {
    circleGenerator.push(css`
    .circle:nth-child(${i}) {
      size: ${initSize + (i - sizeMod)}px;
      border: 13px solid black;
      border-bottom:none;
			border-radius:${initSize + (i - sizeMod)}px ${initSize + (i - sizeMod)}px 0 0;
			width:${initSize + (i - sizeMod)}px;
			height:${(initSize + (i - sizeMod)) / 2}px;
			z-index:  ${16 - i};
      animation: ${animateCircle(i)} 10s infinite ease-in-out;
    }
    `);
  } else {
    circleGenerator.push(css`
    .circle:nth-child(${i}) {
      size: 25px;
      // background:black;
      border-bottom:none;
			border-radius:${initSize + (i - sizeMod)}px ${initSize + (i - sizeMod)}px 0 0;
			width:${initSize + (i - sizeMod)}px;
			height:${(initSize + (i - sizeMod)) / 2}px;
			z-index:  ${16 - i};
      animation: ${animateCircle(i)} 10s infinite ease-in-out;
    }
    `);
  }
};

console.log(circleGenerator)

export const CircleHolder = styled.div`
  width: 500px;
  height:250px;
  position relative;

  .circle {
    border-radius: 100% 100% 0 0;
    position: absolute;
    bottom: 50%;
    left: 50%;
    transform: translateX(-50%);
    transform-origin: bottom center;
  }
  ${circleGenerator}
`;
