import { useState } from "react";
import { NavBar, NavLink } from "./NavComponents";
import ScrollContext from "../../store/context";

const NavComponent = (props: any) => {
  const [navBar, setNavBar] = useState(false)

  let navHeight = document.getElementById("landingPage")?.offsetHeight;

  const navToggle = () => {
    if(navHeight && navHeight < window.scrollY  ) {
      setNavBar(true);
    }else {
      setNavBar(false);
    };
  };

  return (
    <NavBar navBackground={navBar}>
      <NavLink
        to="landingPage"
        spy={true}
        smooth={true}
        duration={400}
      >
        Home
      </NavLink>
      <NavLink
        to="main"
        spy={true}
        smooth={true}
        duration={400}
      >
        About
      </NavLink>
      {/* <li>
        Work
      </li> */}
      <NavLink
        to="contact-form"
        spy={true}
        smooth={true}
        duration={400}
      >
        Contact
      </NavLink>
    </NavBar>
  );
};

export default NavComponent;
