import "./About.scss";
import styled from "styled-components";
import { useState, useEffect, useContext } from "react";
import ScrollContext from "../../store/context";

interface Props {
  scrollPercent: number;
}

const AboutMeTitle = styled.div<Props>`
  transform: translateX(${({ scrollPercent }) => -500 + scrollPercent}%);
`;

const About = () => {
  const [scrollAmount, setScrollAmount] = useState(0);
  const scrollCtx = useContext(ScrollContext);
  const landingTransitioned = scrollCtx.landingTransition;

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
  }, []);

  const handleScroll = (event: any) => {
    setScrollAmount(window.scrollY * 0.1);
  };

  const aboutContent = (
    <section id="about" className="about">
      <div className="about-image"></div>
      <AboutMeTitle scrollPercent={scrollAmount} className="about-title">
        About Me
      </AboutMeTitle>
      <div className="about-me">
        Front-end developer creating memorable and smooth user experiences
      </div>
    </section>
  );

  return <>{landingTransitioned && aboutContent}</>;
};

export default About;
