import { Card, CardTitle } from "../UI/Card";
import { useState } from "react";
import FormInput from "./FormInput";
import FormMessage from "./FormMessage";
import { ContactForm, Button, FormControls, FormError } from "./FormStyles";
import { useEffect } from "react";

const formState: { name: string; email: string; message: string } = {
  name: "",
  email: "",
  message: "",
};

const touchState: { name: boolean; email: boolean; message: boolean} = {
  name: false,
  email: false,
  message: false,
}

const Form = () => {
  const [formData, setFormData] = useState(formState);
  const [isValid, setIsValid] = useState(false);
  const [used, setUsed] = useState(touchState)

  const nameChangeHandler = (
    event: React.ChangeEvent<HTMLInputElement>
  ): void => {
    setFormData((prevState) => ({ ...prevState, name: event.target.value }));

  };

  const emailChangeHandler = (event: React.ChangeEvent<HTMLInputElement>) => {
    setFormData((prevState) => ({ ...prevState, email: event.target.value }));
  };

  const messageChangeHandler = (
    event: React.ChangeEvent<HTMLTextAreaElement>
  ) => {
    setFormData((prevState) => ({ ...prevState, message: event.target.value }));
  };

  useEffect(() => {
    if (isValid) {
      console.log(formData);
    }
  })

  const submitHandler = (event: any) => {
    event.preventDefault();
    Object.keys(touchState).forEach(key =>     setUsed((prevState) => ({...prevState, key: true})));

    if (formData.name.trim() === "" || formData.email.trim() === "" || formData.message.trim() === "") {
      setIsValid(false)
      return;
    };

    setIsValid(true);
    console.log(formData);

  };

  const nameBlurHandler = () => {
    // setUsed(true);

    if (formData.name.trim() === "" || formData.email.trim() === "" || formData.message.trim() === "") {
      setIsValid(false)
      return;
    };
  };

  const emailBlurHandler = () => {
    setUsed(true);

    if (formData.email.trim() === "") {
      setIsValid(false)
      return;
    };
  };

  const messageBlurHandler = () => {
    setUsed(true);

    if (formData.message.trim() === "") {
      setIsValid(false)
      return;
    };
  };

  const invalidCheck = !isValid && used;

  const formComponent = (
    <>
      <CardTitle>Contact Form</CardTitle>
      <ContactForm onSubmit={submitHandler}>
        <FormInput
          id="name"
          errorClass={invalidCheck}
          labelName="Name"
          min={1}
          inputValue={formData.name}
          inputChange={nameChangeHandler}
          onBlur={nameBlurHandler}
        />
        <FormInput
          id="email"
          errorClass={invalidCheck}
          labelName="Email"
          min={1}
          inputValue={formData.email}
          inputChange={emailChangeHandler}
          onBlur={emailBlurHandler}
        />
        <FormMessage
          id="message"
          errorClass={invalidCheck}
          labelName="Message"
          rows={5}
          messageValue={formData.message}
          messageChange={messageChangeHandler}
          onBlur={messageBlurHandler}
        />
        {invalidCheck && <FormError>Please fill in all sections</FormError>}
        <FormControls>
          <Button>Cancel</Button>
          <Button type="submit">Send</Button>
        </FormControls>
      </ContactForm>
    </>
  );

  return (
    <>
      <section id="contact-form">
        <Card>{formComponent}</Card>
      </section>
    </>
  );
};

export default Form;
