import styled from "styled-components";
import backgroundImg from "../images/background.jpg";

export const MainSection = styled.section`
  position: relative;
  padding-top: 5em;

  &:before {
    content: "";
    position: absolute;
    opacity: 0.05;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background-image: url(${backgroundImg});
    background-size: cover;
  }
`;

interface monToggle {
  deviceToggle: boolean;
}

export const MonitorToTablet = styled.div<monToggle>`
  ${({ deviceToggle }) => (deviceToggle ? monitor : tablet)}

  transition: all 0.4s ease;
`;

const monitor = `
color: #000;
position: absolute;
margin-left: -30em;
margin-top: -9em;
width: 213px;
height: 140px;
border-radius: 5%;
border: solid 5px currentColor;
background-color: $highlight-color;

&:before {
  content: '';
  position: absolute;
  left: 85px;
  bottom: -35px;
  width: 30px;
  height: 30px;
  background-color: currentColor;
}

&:after {
  content: '';
  position: absolute;
  left: 63px;
  bottom: -40px;
  width: 70px;
  height: 10px;
  background-color: currentColor;
  border: solid 2px currentColor;
  border-radius: 5px;
}
`;

const tablet = `
position: absolute;
margin-left: -30em;
margin-top: -7em;
width: 105px;
height: 100px;
border-top: solid 5px black;
border-bottom: solid 5px black;
border-radius: 0;


&:before {
  content: '';
  position: absolute;
  top: -20px;
  left: -13px;
  height: 120px;
  width: 100px;
  border: solid 10px black;
  background-color: $primary-color;
  border-radius: 5%;
  z-index: -1;
}

&:after {
  content: '';
  position: absolute;
  bottom: -17.5px;
  left: 40px;
  width: 5px;
  height: 5px;
  border: solid 3px currentColor;
  border-radius: 50%;
}`;
