  left: -25px;
  top: 140px;
  bottom: 100px;
  width: 240px;
  height: 15px;
  border-radius: 0 0 50% 50%;
  border: solid 1px black;
  background-color: black;
}

&:after {
  content: '';
  position: absolute;
  top: 124px;
  left: 180px;
  height: 3px;
  width: 3px;
  border-radius: 50%;
  background-color: red;
}
`;
