import styled from "styled-components";

const circleGenerator = () => {
  for (let i = 1; i < 16; i++) {
    if (i > 1) {
      return `
      &:nth-child(${i}) {
        size: ${31*(i-1)}px;
        border: 13px solid black;
      }
      `
    } else {
      return (`
      size:25px; 
      background:black;`)
    }
  }
};

export const CircleHolder = styled.div`
  width: 500px;
  height:250px;
  position relative;


`;

export const FormCircle = styled.div`
  duration: 10s;

  border-radius: 100% 100% 0 0;
  position: absolute;
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
  transform-origin: bottom center;
`;
