import { useReducer } from "react";
import "./Skills.scss";

const breakPointDefaults = {
  one: false,
  two: false,
  three: false,
  four: false,
  five: false,
  six: false,
};
const DummyData = [
  {
    id: "s6",
    name: "Typescript",
    num: 6,
  },
  {
    id: "s5",
    name: "React",
    num: 5,
  },
  {
    id: "s4",
    name: "JavaScript",
    num: 4,
  },
  {
    id: "s3",
    name: "SCSS",
    num: 3,
  },
  {
    id: "s2",
    name: "CSS",
    num: 2,
  },
  {
    id: "s1",
    name: "HTML",
    num: 1,
  },
];

type breakPoint1 = {
  ONE: "breakpoint_1",
}
type breakPoint2 = {
  TWO: "breakpoint_2",
}
type breakPoint3 = {
  THREE: "breakpoint_3",
}
type breakPoint4 = {
  FOUR: "breakpoint_4",
}
type breakPoint5 = {
  FIVE: "breakpoint_5",
}
type breakPoint6 = {
  SIX: "breakpoint_6",
}

type breakPoint = breakPoint1 | breakPoint2 | breakPoint3 | breakPoint4 | breakPoint5 | breakPoint6;


const scrollReducer = (state:typeof breakPointDefaults, action: breakPoint) => {
  switch (action.type) {

  }
};

const Skills = () => {
  const skillSection = document.getElementById("skills");
  const sectionTop = skillSection?.offsetTop;
  const sectionHeight = skillSection?.clientHeight;
  const scrollBreakPoint = sectionHeight! / DummyData.length;

  const [breakPointState, dispatchScrollState] = useReducer(
    scrollReducer,
    breakPointDefaults
  );

  window.addEventListener("scroll", () => {
    DummyData.forEach((element) => {
      if (window.scrollY > sectionTop! + scrollBreakPoint * element.num) {
        dispatchScrollState({type: breakPoint.ONE});
      }
    });
  });

  const scrollCondition1 = breakPointState.breakPoint1 ? "slide-out" : "";
  const skillList = DummyData.map((skill) => (
    <div
      className={`skill-card skill-card-${skill.num} ` + scrollCondition1}
      id={`skill${skill.num}`}
    >
      {skill.name}
    </div>
  ));

  return (
    <div className="skills" id="skills">
      <div className="image-container" id="imageContainer">
        {skillList}
      </div>
    </div>
  );
};

export default Skills;
