import styled from "styled-components";
import { highlightColor, primaryColor } from "../UI/themeStyles";

interface monToggle {
  deviceToggle: boolean;
}

export const DeviceWrapper = styled.div`
  width: 80%;
  position: relative;
  height: 30vmin;
  border: 1px solid red;
`;

export const MonitorToTablet = styled.div<monToggle>`
  ${({ deviceToggle }) => (deviceToggle ? monitor : monitor)}
  transition: all 0.4s ease;
`;
export const PhoneToLaptop = styled.div<monToggle>`
  ${({ deviceToggle }) => (deviceToggle ? phone : laptop)}
  transition: all 0.4s ease;
`;

const monitor = `
color: #000;
position: absolute;
top:5%;
left 10%;
width: 20vw;
padding: 5.625vw;
border-radius: 5%;
border: solid 5px black;
background-color: ${highlightColor};

&:before {
  content: '';
  position: absolute;
  top: 100%;
  left: 43%;
  width: 3vw;
  height: 2vw;
  background-color: black;
}

&:after {
  content: '';
  position: absolute;
  transform: translate(1.3vw, 7.5vw);
  width: 5vw;
  height: .7vw;
  background-color: currentColor;
  border: solid 2px currentColor;
  border-radius: 5px;
}
`;

const tablet = `
color: #000;
position: absolute;
left: 20%;
top: 15%;
width: 3.525vw;
padding: 4.2vw;
border-radius: 0;
border: solid 5px black;
background-color: ${highlightColor};


&:before {
  content: '';
  position: absolute;
  transform: translate(-50%,-50%);
  width: 8.5vw;
  height: 10.5vw;
  border: solid 5px black;
  background-color: ${primaryColor};
  border-radius: 5%;
  z-index: -10;
}

&:after {
  content: '';
  position: absolute;
  transform:translate(-0.3vw,4.65vw);
  width: 0.1vw;
  height: 0.1vw;
  border: solid 3px currentColor;
  border-radius: 50%;
}`;

const phone = `
color: #000;
position: absolute;
top: 25%;
right: 15%;
width: 50.625px;
height: 90px;
border-radius: 10%;
border: solid 5px black;
background-color: ${highlightColor};

&:before {
  content: '';
  position: absolute;
  left: 17.5px;
  top: 5px;
  width: 5px;
  height: 2.5px;
  background-color: black;
}

&:after {
  content: '';
  position: absolute;
  bottom: 5px;
  left: 19px;
  height: 5px;
  width: 5px;
  border-radius: 50%;
  background-color: black;
}
`;

const laptop = `
color: #000;
position: absolute;
right:10%;
top: 10%;
width: 213px;
height: 140px;
border-radius: 5%;
border: solid 10px black;
background-color: ${highlightColor};

&:before {
  content: '';
  position: absolute;
  left: -25px;
  top: 140px;
  bottom: 100px;
  width: 240px;
  height: 15px;
  border-radius: 0 0 50% 50%;
  border: solid 1px black;
  background-color: black;
}

&:after {
  content: '';
  position: absolute;
  top: 124px;
  left: 180px;
  height: 3px;
  width: 3px;
  border-radius: 50%;
  background-color: red;
}
`;
