import { FunctionComponent, useContext } from "react";
import ScrollContext from "../../store/context";
import styled from "styled-components";
import "./LandingPage.scss";
// currently not used
// interface Props {
//   scroll: boolean;
// }

const section = ({className, children}:{className:string, children:any}) => 
  (<section className={className}>
    {children}
  </section>
);

const h2 = ({className, children}:{className:string, children:any}) => (
  <h2 className={className}>
    {children}
  </h2>
);

const LandingSection = styled.section`
  width: 100%;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
  background: linear-gradient((to right, ${props => props.theme.primaryColor} 50%, ${props => props.theme.secondaryColor} 50%));
  background-size: 200%, 100%;
  @include transition-ease;
`;

const HiddenHeader = styled.h2`
    color: ${props => props.theme.primaryColor}
`;


const LandingPage: FunctionComponent = () => {
  const scrollCtx = useContext(ScrollContext);
  const classes = "landing" + (scrollCtx.scroll ? " night" : "");
  // scroll={scrollCtx.scroll} is currently redundant

  return (
    <LandingSection id="landingPage" className={classes}>
      <header className="name">
        <h1>I'm John</h1>
        <HiddenHeader >a web developer</HiddenHeader>
      </header>
    </LandingSection>
  );
};
//
export default LandingPage;
