import { useContext, useState } from "react";
import ScrollContext from "../../store/context";
import { Card, CardTitle } from "../UI/Card";
import { CircleHolder } from "./FormButtonStyles";
import { NameInput } from "./FormStyles";

const Form = () => {
  const scrollCtx = useContext(ScrollContext);
  const scroll = scrollCtx.scroll;
  const [clicked, setClicked] = useState(false)

  const clickHandler = () => {
    setClicked(!clicked)
  }

  const formCircle = (
    <CircleHolder onClick={clickHandler}>
      <div className="circle">Interested?</div>
      <div className="circle"></div>
      <div className="circle"></div>
      <div className="circle"></div>
    </CircleHolder>
  )

  const form = (
    <>
      <CardTitle>Contact Me!</CardTitle>
      <form>
        <div className="form-control">
          <div className="first-name">
            <NameInput type="text" value="First Name"/>
          </div>
            <NameInput className="last-name" value="Last Name"/>
        </div>
      </form>
    </>
  )

  return (
    <>
      {scroll && (
        <section id="contact-form">
          <Card>{clicked ? form : formCircle}</Card>
        </section>
      )}
    </>
  );
};

export default Form;
