sourcesContent:
import styled from "styled-components";

export const Footer = styled.footer`
  display: flex;
  padding: 3em 2em 3em 2em;
  background-color: $secondary-color;
  color: white;
  width: 100%;
  h3 {
    display: flex;
    align-items: center;
    font-size: 3.5rem;
  }

  ul {
    list-style-type: none;
    padding: 2em 0 2em 0;
  }

  .socials {
    width: 60%;
    border-right: 2px solid white;
    & p {
      font-size: 0.8rem;
    }

    & ul {
      display: flex;
      align-items: center;
    }

    & li {
      margin: 0 1.5em 0 0;
    }
  }

  .info {
    display: flex;
    align-items: center;
    width: 40%;
    padding-left: 5%;
    & li {
      font-size: 1.3rem;
      padding-bottom: 1em;
    }
  }
`;
