import { useState, useRef, useLayoutEffect } from "react";
import "./Skills.scss";

const Skills = () => {
  const [show, doShow] = useState({
    itemOne: false,
    itemTwo: false,
    itemThree: false
  });
  const ourRef = useRef(null),
    anotherRef = useRef(null),
    refThree = useRef(null);

  useLayoutEffect(() => {
    const topPos = (element:any) => element.getBoundingClientRect().top;
    const div1Pos = topPos(ourRef.current),
      div2Pos = topPos(anotherRef.current),
      div3Pos = topPos(refThree.current);

    const onScroll = () => {
      const scrollPos = window.scrollY + window.innerHeight;
      if (div1Pos < scrollPos) {
        doShow(state => ({ ...state, itemOne: true }));
      } else if (div2Pos < scrollPos) {
        doShow(state => ({ ...state, itemTwo: true }));
      } else if (div3Pos < scrollPos) {
        doShow(state => ({ ...state, itemThree: true }));
      }
    };

    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  return (
    <div className="skills" id="skills">
      <div className="image-container" id="imageContainer">
        <div className="skill-card skill-card-6" id="skill6">
          TypeScript
        </div>
        <div className="skill-card skill-card-5" id="skill5">
          React
        </div>
        <div className="skill-card skill-card-4" id="skill4">
          JS
        </div>
        <div className="skill-card skill-card-3" animate={show.itemThree} ref={refThree} id="skill3">
          SCSS
        </div>
        <div className="skill-card skill-card-2" animate={show.itemTwo} ref={anotherRef} id="skill2">
          CSS
        </div>
        <div className="skill-card skill-card-1" animate={show.itemOne} ref={ourRef} id="skill1">
          HTML
        </div>
      </div>
    </div>
  );
};

export default Skills;
