import { Card, CardTitle } from "../UI/Card";
import {useForm} from "react-hook-form";
import {
  DataInput,
  MessageInput,
  ContactForm,
  Button,
  FormControls,
} from "./FormStyles";

type FormData = {
  name: string;
  email: string;
  message: string;
}

const Form = () => {
  const {register, handleSubmit} = useForm<FormData>();

  const formSubmit = (data: any) => {
    console.log(data)
  };

  const formComponent = (
    <>
      <CardTitle>Contact Form</CardTitle>
      <ContactForm className="form-control" onSubmit={handleSubmit(formSubmit)}>
        <DataInput placeholder="Name" type="text" {...register("name")}/>
        <DataInput placeholder="Email" type="text" {...register("email")}/>
        <MessageInput placeholder="Message"  {...register("message")}/>
        <FormControls>
          <Button>Cancel</Button>
          <Button type="submit">Send</Button>
        </FormControls>
      </ContactForm>
    </>
  );

  return (
    <>
        <section id="contact-form">
          <Card>{formComponent}</Card>
        </section>
    </>
  );
};

export default Form;
