import { useContext } from "react";
import ScrollContext from "../../store/context";
import { Card, CardTitle } from "../UI/Card";
import { CircleHolder } from "../Landing/CircleAnimation";
import {
  DataInput,
  ContactForm,
  MessageInput,
  Button,
  FormControls,
} from "./FormStyles";

const Form = () => {
  const scrollCtx = useContext(ScrollContext);
  const scroll = scrollCtx.scroll;

  const formComponent = (
    <>
      <CardTitle>Contact Form</CardTitle>
      <ContactForm className="form-control">
        <DataInput type="text" value="Name" />
        <DataInput type="text" value="Email" />
        <MessageInput rows={5} value="Message" />
        <FormControls>
          <Button>Cancel</Button>
          <Button type="submit">Send</Button>
        </FormControls>
      </ContactForm>
    </>
  );

  return (
    <>
      {scroll && (
        <section id="contact-form">
          <Card>{formComponent}</Card>
        </section>
      )}
    </>
  );
};

export default Form;
