import { useContext } from "react";
import ScrollContext from "../../store/context";
import {
  IoLogoGithub,
  IoLogoInstagram,
  IoLogoLinkedin,
  IoLogoTwitter,
} from "react-icons/io";
import { AiOutlineFacebook, AiOutlineCopyrightCircle } from "react-icons/ai";
import { FaCodepen } from "react-icons/fa";
import { Footer } from "./FooterStyles";
import { Facebook } from "./FooterStyles";

const FootComponent = () => {
  const scrollCtx = useContext(ScrollContext);
  const scroll = scrollCtx.scroll;
  const footerContent = (
    <Footer className="footer">
      <div className="socials">
        <ul>
          <li>
            <p>You can find me socially on these platforms: </p>
          </li>
          <li>
            <Facebook />
          </li>
          <li>
            <IoLogoInstagram size="1.5vw" />
          </li>
          <li>
            <IoLogoTwitter size="1.5vw" />
          </li>
          <li>
            <IoLogoLinkedin size="1.5vw" />
          </li>
          <li>
            <IoLogoGithub size="1.5vw" />
          </li>
          <li>
            <FaCodepen size="1.5vw" />
          </li>
        </ul>
        <h3>
          <AiOutlineCopyrightCircle size="3vw" />
          Jonathan Yan
        </h3>
      </div>
      <div className="info">
        <ul>
          <li>Contact</li>
          <li>About me</li>
          <li>Projects</li>
        </ul>
      </div>
    </Footer>
  );
  return <>{scroll && footerContent}</>;
};

export default FootComponent;
