import React from "react";
import "./Skills.scss";

type breakPointStates = {
  breakPoint1: boolean,
  // breakPoint2: boolean,
  // breakPoint3: boolean,
  // breakPoint4: boolean,
  // breakPoint5: boolean,
  // breakPoint6: boolean,
};

type breakPointTriggered = {
  type: 'bpTriggered';
  switchTo: boolean;
}


function reducer(state: breakPointStates, action: breakPointTriggered): breakPointStates {
  if (action.type === 'bpTriggered') {
    return {breakPoint1: true}
  } else {
    return {breakPoint1: false};
  }
}

const breakPointDefaults = {
  defaultPoint1: false,
  // breakPoint2: false,
  // breakPoint3: false,
  // breakPoint4: false,
  // breakPoint5: false,
  // breakPoint6: false,
};
const DummyData = [
  {
    id: "s6",
    name: "Typescript",
    num: 6,
  },
  {
    id: "s5",
    name: "React",
    num: 5,
  },
  {
    id: "s4",
    name: "JavaScript",
    num: 4,
  },
  {
    id: "s3",
    name: "SCSS",
    num: 3,
  },
  {
    id: "s2",
    name: "CSS",
    num: 2,
  },
  {
    id: "s1",
    name: "HTML",
    num: 1,
  },
];

const Skills = () => {
  const skillSection = document.getElementById("skills");
  const sectionTop = skillSection?.offsetTop;
  const sectionHeight = skillSection?.clientHeight;
  const scrollBreakPoint = sectionHeight! / DummyData.length;

  const [breakPointState, dispatchScrollState] = React.useReducer(reducer, {breakPoint1: false})

  window.addEventListener("scroll", () => {
    DummyData.forEach((element) => {
      if (window.scrollY > sectionTop! + scrollBreakPoint * element.num) {
        dispatchScrollState();
      }
    });
  });

  const scrollCondition1 = breakPointState.breakPoint1 ? "slide-out" : "";
  const skillList = DummyData.map((skill) => (
    <div
      className={`skill-card skill-card-${skill.num} ` + scrollCondition1}
      id={`skill${skill.num}`}
    >
      {skill.name}
    </div>
  ));

  return (
    <div className="skills" id="skills">
      <div className="image-container" id="imageContainer">
        {skillList}
      </div>
    </div>
  );
};

export default Skills;
