import styled from "styled-components";
import { StyledIconBase } from "@styled-icons/styled-icon";
import {device} from "../UI/themeStyles";
import {Html5, Css3, Javascript, Sass, Typescript, ReactLogo} from "@styled-icons/simple-icons";


export const IconStyle = styled.div`
display: flex;

${StyledIconBase} {
  width: 5em;
  height: 5em;
  margin: 0 1vw 0 1vw;

  @media only ${device.mobileS}{
    width: 3em;
    height: 3em;
  }
}
`;

const entryAnimation = `
  transition: all 10s ease; 
@keyframes onInitialView {
  0% {
    transform:translateY(-10vw)
  }
  100% {
    transform:translateY(0)
  }
}
`;

export const Html = styled(Html5)`
  color:#D94625;
  ${entryAnimation}
`;
export const Css = styled(Css3)`
  color:#2168A6;
  ${entryAnimation}
`;
export const Scss = styled(Sass)`
  color:#CD669A;
  ${entryAnimation}
`;
export const JavaScript = styled(Javascript)`
  color:#F0DB4F;
  background-color: black;
  ${entryAnimation}
`;
export const TypeScript = styled(Typescript)`
  color:#61DBFB;
  ${entryAnimation}
`;
export const React = styled(ReactLogo)`
  color:#007ACC;
  ${entryAnimation}
`;
