import styled from "styled-components";

interface Props {
  scroll: boolean;
  scrollPercent: number;
}

export const Header = styled.header`
  text-align: center;
  transform: translateY(-20%);
`;

const section = ({
  className,
  children,
}: {
  className: string;
  children: any;
}) => <section className={className}>{children}</section>;

export const LandingSection = styled.section<Props>`
  width: 100%;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
  background: linear-gradient(
    to right,
    ${(props) => props.theme.primaryColor} 50%,
    ${(props) => props.theme.secondaryColor} 50%
  );
  background-size: 200%, 100%;
  transition: all 2.5s ease;
  background-position: ${({ scroll }) => (scroll ? "right" : "left")};
`;

export const AboutMeTitle = styled.div<Props>`
  transform: translateX(${({ scrollPercent }) => -50 + scrollPercent}%);
`;
