import NavBar from "./NavBar";
import ScrollContext from "../../store/context";

const Layout = (props: any) => {
  return (
    <ScrollContext.Provider value={{scroll:false}}>
      <main>
        <NavBar />
        {props.children}
      </main>
    </ScrollContext.Provider>
  );
};

export default Layout;
