import styled from "styled-components";
import backgroundImg from "../images/background.jpg";

export const MainSection = styled.section`
  position: relative;
  padding-top: 5em;

  &:before {
    content: "";
    position: absolute;
    opacity: 0.05;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background-image: url(${backgroundImg});
    background-size: cover;
  }
`;

interface monToggle {
  deviceToggle: boolean;
}

export const MonitorToTablet = styled.div<monToggle>`
  ${({ deviceToggle }) => (deviceToggle ? monitorTablet : phoneLaptop)}

  transition: all 0.4s ease;
`;

const monitorTablet = `color: #000;
position: absolute;
margin-left: -30em;
margin-top: -9em;
width: 213px;
height: 140px;
border-radius: 5%;
border: solid 5px currentColor;
background-color: $highlight-color;

&:before {
  content: '';
  position: absolute;
  left: 85px;
  bottom: -35px;
  width: 30px;
  height: 30px;
  background-color: currentColor;
}

&:after {
  content: '';
  position: absolute;
  left: 63px;
  bottom: -40px;
  width: 70px;
  height: 10px;
  background-color: currentColor;
  border: solid 2px currentColor;
  border-radius: 5px;
}`;

const phoneLaptop = `
color: #000;
position: absolute;
margin-left: 30em;
margin-top: -6em;
width: 50.625px;
height: 90px;
border-radius: 10%;
border: solid 5px currentColor;
background-color: $highlight-color;

&:before {
  content: '';
  position: absolute;
  left: 17.5px;
  top: 5px;
  width: 5px;
  height: 2.5px;
  background-color: currentColor;
}

&:after {
  content: '';
  position: absolute;
  bottom: 5px;
  left: 19px;
  height: 5px;
  width: 5px;
  border-radius: 50%;
  background-color: currentColor;
}

&.toggled {
  color: #000;
  position: absolute;
  margin-left: 35em;
  margin-top: -8.5em;
  width: 213px;
  height: 140px;
  border-radius: 5%;
  border: solid 10px currentColor;

  &:before {
    content: '';
    position: absolute;
    left: -25px;
    top: 140px;
    bottom: 100px;
    width: 240px;
    height: 15px;
    border-radius: 0 0 50% 50%;
    border: solid 1px black;
  }

  &:after {
    content: '';
    position: absolute;
    top: 124px;
    left: 180px;
    height: 3px;
    width: 3px;
    border-radius: 50%;
    background-color: red;
  }
}`;
