import { Card, CardTitle, CardText } from "../UI/Card";
import {
  Css,
  Html,
  IconStyle,
  JavaScript,
  React,
  Scss,
  TypeScript,
} from "./TechStyles";
import { useEffect } from "react";
import { useState } from "react";

const TechStack = () => {
  const [initialView, setInitialview] = useState(false);
  useEffect(() => {
    const techStack = document.getElementById("techStack");

    window.addEventListener("scroll", () => {
      console.log(techStack!.top);
      if (window.scrollY > techStack!.scrollTop) {
        setInitialview(true);
      }
    });
  });
  const LogoHolder = (
    <>
      <Html title="Html" />
      <Css title="Css" />
      <JavaScript title="Javascript" />
      <Scss title="Sass" />
      <TypeScript title="Typescript" />
      <React title="React" />
    </>
  );

  return (
    <Card id="techStack">
      <CardTitle>Tech Stack</CardTitle>
      <CardText>
        I use multiple technologies such as HTML, CSS and JavaScript coupled
        with superscripts like TypeScript and SCSS/SASS. For data management I
        use MongoDB or Firebase.
      </CardText>
      <IconStyle>
        {initialView ? LogoHolder: null}
      </IconStyle>
    </Card>
  );
};

export default TechStack;
