import styled, { css, keyframes } from "styled-components";

const circleGenerator = [];

const animateCircle = (i: number) => keyframes`
  0% {
    transform:translateX(-50%) rotate(0deg);
  }
  100% {
    transform:translateX(-50%) rotate(${-i*360}deg);
  }
`;

const initSize = 20;
const scaleMod = 0.9;

for (let i = 1; i < 16; i++) {
  if (i > 1) {
    circleGenerator.push(css`
    .circle:nth-child(${i}) {
      size: ${initSize + (1 * (i - i*scaleMod))}em;
      border: 13px solid black;
      border-bottom:none;
			border-radius:${initSize * (i - i*scaleMod)}em ${initSize * (i - i*scaleMod)}em 0 0;
			width:${initSize * (i - i*scaleMod)}em;
			height:${(initSize * (i - i*scaleMod)) / 2}em;
			z-index:  ${16 - i};
      // animation: ${animateCircle(i)} 10s infinite ease-in-out;
    }
    `);
  };
};

export const CircleHolder = styled.div`
  width: 500px;
  height:250px;
  position relative;

  .circle {
    border-radius: 100% 100% 0 0;
    position: absolute;
    bottom: 50%;
    left: 50%;
    transform: translateX(-50%);
    transform-origin: bottom center;
  }
  ${circleGenerator}
`;
